```php
<?php
// Create a date object
$d = new DateTime('2024-04-27');
echo $d->format('Y-m-d'); 

// Output : 2024-04-27

// Create a time object 
$t = new DateTime('15:30:45');
echo $t->format('H:i:s'); 

// Output : 15:30:45

// Create a datetime object
$dt = new DateTime('2024-04-27 15:30:45');
